import React from "react";
import { useState } from "react";
import {useNavigate} from "react-router-dom";

function Login({setUser}){
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault()
        fetch("/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({username, password})
        })
        .then((r) => r.json())
        .then((data) => {
            setUser(data)
            navigate("/")
        }
        )
    }
    return (
<div className="flex h-screen items-center justify-center bg-gradient-to-b from-gray-800 to-gray-900 text-white">
  <form onSubmit={handleSubmit} className="md:flex h-80 text-lg flex flex-col bg-slate-400 border border-gray-900 p-6 rounded-lg justify-center items-center">
    <label className="text-xl mb-2" htmlFor="username">Username:</label>
    <input onChange={(e) => setUsername(e.target.value)} value={username} type="text" id="username" name="username" className="w-64 h-12 px-4 border border-gray-600 rounded-lg text-black mb-4"></input>
    <label className="text-xl mb-2" htmlFor="password">Password:</label>
    <input onChange={(e) => setPassword(e.target.value)} value={password} type="password" className="w-64 h-12 px-4 border border-gray-600 rounded-lg text-black mb-4" name="password"></input>
    <button className="w-64 h-12 bg-gray-600 text-white rounded-lg hover:bg-gray-700" type="submit">Login</button>
  </form>
</div>
    )
}

export default Login
