import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";

function ProjectCard({tab, setProjectToView, user}){
    const navigate = useNavigate()

    const handleClick = () => {
        if (tab.user_id === user.id){
            setProjectToView(tab)
            navigate(`/edit_project/${tab.id}`)
        } else {
            setProjectToView(tab)
            navigate(`/project/${tab.id}`)
        }
    }
    return (
        <div onClick={handleClick} className="border rounded-md flex flex-col w-48 sm:w-64 md:w-96 lg:w-128 xl:w-160 2xl:w-192">
          <div className="flex-grow">
            <div className="font-bold">Artist:</div>
            <div>{tab.artist}</div>
          </div>
          <div className="flex-grow">
            <div className="font-bold">Song:</div>
            <div>{tab.title}</div>
          </div>
          <div className="flex-grow">
            <div className="font-bold">Average Rating:</div>
            <div>{tab.average_rating}</div>
          </div>
        </div>
      )
    }

export default ProjectCard
