import React, { useState } from "react";
import { NavLink } from "react-router-dom";

function NavBar({ user, projectToView }) {
    const [isClicked, setIsClicked] = useState(false)

    const handleLogOut = () => {
        fetch("/logout", {
            method: "POST",
        })
            .then(window.location.reload())
    }

    return (
        <>
            {user ? (
                    <div className="left-0 top-0 bg-gray-950 h-screen flex flex-col items-center transition-transform ease-in-out duration-500 transform-gpu -translate-x">
                        <NavLink to="/" className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 hover:scale-110 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2">Home</NavLink>
                        <NavLink to="/create" className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2">Create</NavLink>
                        <NavLink to="/your_projects" className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2">Your Projects</NavLink>
                        {projectToView ? (<NavLink to="/reviews/:id" className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2">Reviews</NavLink>) : null}
                        {user ? (<button onClick={handleLogOut} className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 rounded-md transition duration-200 ease-in-out bg-gray-700 shadow-md mx-2 my-2">Logout</button>) : null}
                        <button onClick={() => setIsClicked(!isClicked)}>Woooop</button>
                        <button onClick={(e) => setIsClicked(!isClicked)}>DropDown</button>
                    </div>
            ) : null}
        </>
    )
}

export default NavBar;
