import React from "react";
import { useState } from "react";
import {useNavigate} from "react-router-dom";

function Login({setUser}){
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault()
        fetch("/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({username, password})
        })
        .then((r) => r.json())
        .then((data) => {
            setUser(data)
            navigate("/")
        }
        )
    }
    return (
// {/* <div className="flex min-h-screen bg-gradient-to-b from-gray-800 to-gray-900 items-center justify-center text-white"> */}
  <form onSubmit={handleSubmit} className="md:flex h-64 text-md flex flex-col bg-gray-700 border border-gray-900 p-6 rounded-lg justify-center text-center w-full md:w-2/3 lg:w-1/2">
    <label className="mb-2">Username:</label>
    <input onChange={(e) => setUsername(e.target.value)} value={username} type="text" id="username" name="username" className="border border-gray-600 rounded-lg text-black px-2 py-1 mb-4 text-center"></input>
    <label className="mb-2">Password:</label>
    <input onChange={(e) => setPassword(e.target.value)} value={password} type="password" className="border border-gray-600 rounded-lg text-black px-2 py-1 mb-4 text-center" name="password"></input>
    <button className="border border-gray-600 rounded-lg px-4 py-2 bg-color-" type="submit" >Login</button>
  </form>
// </div>
    )
}

export default Login
