import React from "react";
import { useState } from "react";
import {useNavigate} from "react-router-dom";

function Login({setUser}){
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault()
        fetch("/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({username, password})
        })
        .then((r) => r.json())
        .then((data) => {
            setUser(data)
            navigate("/")
        }
        )
    }
    return (
<div className="min-h-screen bg-gradient-to-b from-gray-800 to-gray-900 text-white flex items-center justify-center px-4 sm:px-6 lg:px-8">
    <form onSubmit={handleSubmit} className="w-full max-w-md bg-slate-400 border border-gray-900 p-6 rounded-lg text-center">
        <label className="block font-medium text-md text-gray-200 mb-2" for="username">Username:</label>
        <input onChange={(e) => setUsername(e.target.value)} value={username} type="text" id="username" name="username" className="border border-gray-600 rounded-lg px-3 py-2 text-black mb-3 w-full"></input>
        <label className="block font-medium text-md text-gray-200 mb-2" for="password">Password:</label>
        <input onChange={(e) => setPassword(e.target.value)} value={password} type="password" className="border border-gray-600 rounded-lg px-3 py-2 text-black mb-3 w-full" name="password"></input>
        <button className="bg-gray-200 hover:bg-gray-300 py-2 px-4 rounded-lg text-gray-800 font-medium" type="submit" >Login</button>
    </form>
</div>
    )
}

export default Login
