import React, { useEffect, useRef } from "react";
import * as Tone from "tone";
import { useState } from "react";
import EditMeasure from "./EditMeasure";
import { useNavigate, useParams } from "react-router-dom";
import ConfigTuning from "./ConfigTuning";
import ConfigDuration from "./ConfigDuration";

function EditProject({ project, setProjectToView }) {
    const [measures, setMeasures] = useState([])
    const [duration, setDuration] = useState()
    const [tabData, setTabData] = useState([])
    const [newTabData, setNewTabData] = useState([])
    const measureCountRef = useRef(0)
    const navigate = useNavigate()
    const params = useParams()

    useEffect(() => {
        fetch(`/tabs/${params.id}`)
            .then(r => r.json())
            .then(data => {
                setProjectToView(data)
            })
    }, [])
    console.log(duration)

    // const synth = new Tone.Synth().toDestination();
    // const note = Tone.Frequency('C4').toNote();
    // synth.triggerAttackRelease(note, "4n");

    const handleClick = () => {
        setMeasures([...measures, { id: measures.length }])
        setNewTabData([...newTabData, { measure: measureCountRef.current + measures.length, beat: 1, string: 1, fret: "" }])
    }

    const handleSelectChange = (e) => {
        setDuration(e.target.value)
    }

    const sortedTabData = (data) => {
        return data.sort((a, b) => {
            if (a.measure < b.measure) {
                return -1;
            } else if (a.measure > b.measure) {
                return 1;
            } else {
                return a.beat - b.beat;
            }
        })
    }

    useEffect(() => {
        fetch(`/tab_data/${project?.id}`)
            .then((r) => r.json())
            .then((data) => {
                if (data.length < 1) {
                    setNewTabData([...newTabData, { measure: measureCountRef.current + measures.length, beat: 1, string: 1, fret: "" }])
                } else {
                    setTabData(sortedTabData(data))
                }
            })
    }, [project])

    const groupedMeasures = tabData ? tabData.reduce((r, a) => {
        const measure = a.measure
        if (!r[measure]) {
            r[measure] = []
        }
        r[measure].push(a)
        return r
    }, {}) : null

    const measuresToDisplay = groupedMeasures ? Object.keys(groupedMeasures).map((measure) => {
        const notes = groupedMeasures[measure]
        const count = measure
        measureCountRef.current = parseInt(count) + 1
        return <EditMeasure project_id={project?.id} bpm={project?.bpm} key={count} tabData={notes} setTabData={setTabData} duration={duration} measure={count} />
    }) : setNewTabData([...newTabData, { measure: measureCountRef.current + measures.length, beat: 1, string: 1, fret: "" }])

    const newGroupedMeasures = newTabData ? newTabData.reduce((r, a) => {
        const measure = a.measure
        if (!r[measure]) {
            r[measure] = []
        }
        r[measure].push(a)
        return r
    }, {}) : null

    const newMeasuresToDisplay = newGroupedMeasures ? Object.keys(newGroupedMeasures).map((measure) => {
        const notes = newGroupedMeasures[measure]
        const count = measure
        return <EditMeasure key={count} project_id={project?.id} bpm={project.bpm} tabData={notes} setTabData={setNewTabData} duration={duration} measure={count} />
    }) : null

    const handleSubmitClick = () => {
        const tabDataArray = sortedTabData([...tabData.filter((data) => !(isNaN(data.fret))), ...newTabData.filter((data) => "fret" in data && data.fret !== null && data.fret !== '' && !(isNaN(data.fret)))])
        // console.log(tabDataArray)
        // const tuning = [64, 59, 55, 50, 45, 40];
        // const synth = new Tone.PolySynth().toDestination();

        // const play = () => {
        //     tabDataArray.forEach(note => {
        //         const frequency = Tone.Frequency(tuning[note.string - 1] + note.fret, "midi").toFrequency();
        //         const time = note.time + Tone.now();
        //         const duration = "8n";
        //         synth.triggerAttackRelease(frequency, duration, time);
        //     });
        // };
        // play()
    }

    const handlePlayClick = () => {
        const tabDataArray = sortedTabData([...tabData.filter((data) => !(isNaN(data.fret))), ...newTabData.filter((data) => "fret" in data && data.fret !== null && data.fret !== '' && !(isNaN(data.fret)))])
        // const tuning = [64, 59, 55, 50, 45, 40];
        const tuning = project?.tuning.split("").reduce((acc, note, i) => {
            acc[6 - i] = ConfigTuning[`string${6-i}`][note];
            return acc;
          }, {});
          console.log(tuning)
        const synth = new Tone.PolySynth().toDestination();

        // const play = () => {
        //     tabDataArray.forEach(note => {
        //         const frequency = Tone.Frequency(tuning[note.string] + note.fret, "midi").toFrequency();
        //         const time = note.time + Tone.now();
        //         const duration = ConfigDuration[note.duration];
        //         synth.triggerAttackRelease(frequency, duration, time);
        //     });
        // };
        const part = new Tone.Part((time, note) => {
            const frequency = Tone.Frequency(tuning[note.string] + note.fret, "midi").toFrequency();
            const duration = ConfigDuration[note.duration];
            synth.triggerAttackRelease(frequency, duration, time);
            }, tabDataArray).start(0);

        const start = () => Tone.Transport.start();
        const stop = () => {
            Tone.Transport.stop()
            part.stop()
            part.clear()
        }
        const startButton = document.querySelector("#start");
        const stopButton = document.querySelector("#stop");
        startButton.addEventListener("click", start);
        stopButton.addEventListener("click", stop);
    }

    handlePlayClick()

    const handleReviews = () => {
        navigate(`/reviews/${project?.id}`)
    }

    const handleHome = () => {
        navigate(`/`)
    }

    const handleYourProjects = () => {
        navigate(`/your_projects`)
    }

    return (
        <div className="relative">
            <div className="absolute top-0 right-0 mt-2 mr-2 flex gap-4">
                <button onClick={handleReviews} className="text-2xl border border-white text-white rounded-md px-4 py-2 hover:bg-white hover:text-gray-800">Reviews</button>
                <button onClick={handleYourProjects} className="text-2xl border border-white text-white rounded-md px-4 py-2 hover:bg-white hover:text-gray-800">Your Projects</button>
                <button onClick={handleHome} className="text-2xl border border-white text-white rounded-md px-4 py-2 hover:bg-white hover:text-gray-800">Home</button>
            </div>
            <p className="text-4xl font-bold leading-tight mb-2 sm:text-5xl md:text-6xl">{project?.title}</p>
            <p className="text-lg sm:text-xl md:text-2xl">Artist: {project?.artist}</p>
            <p className="text-lg sm:text-xl md:text-2xl">Tuning: {project?.tuning}</p>
            <p className="text-lg sm:text-xl md:text-2xl">Capo: {project?.capo}</p>

            <div className="flex flex-wrap">
                {measuresToDisplay}
                {newMeasuresToDisplay}
            </div>
            <div className="flex gap-4">
                <button onClick={handleClick} className="text-2xl border border-white text-white rounded-md px-4 py-2 hover:bg-white hover:text-gray-800">Add Measure</button>
                <button onClick={handleSubmitClick} id='asdf' className="text-2xl border border-white text-white rounded-md px-4 py-2 hover:bg-white hover:text-gray-800">Submit data</button>
                {/* <button className="text-2xl border border-white text-white rounded-md px-4 py-2 hover:bg-white hover:text-gray-800" onClick={handlePlayClick}>Play</button> */}
                <button id="start">Play</button>
                <button id='stop'>Pause</button>
                <select onChange={handleSelectChange} className="w-full py-2 px-3 border border-gray-400 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:border-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent md:w-auto">
                    <option>Eighth</option>
                    <option>Quarter</option>
                    <option>Half</option>
                    <option>Whole</option>
                </select>
            </div>
        </div>

    )
}

export default EditProject
