import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";
import UserProjects from "./UserProjects";
import FeaturedTablature from "./FeaturedTablature";

function LandingPage({userTabs, featuredTabs, setUser, user, setProjectToView, setUserTabs, setFeaturedTabs}){
    const navigate = useNavigate()

    useEffect(() => {
        fetch("/check_session")
        .then((r) => {
            if (r.ok) {
                r.json().then((user) => {
                    setFeaturedTabs(user.all_tabs)
                    setUser(user)
                    if (user.tabs) {
                        setUserTabs(user.tabs)
                    }
                })
            } else {
                navigate("/login")
            }
        })
    }, [])

    const handleLogOut = () => {
        fetch("/logout", {
            method: "POST",
        })
        .then(window.location.reload())
    }

    const handleNewProject = () => {
        navigate("/create")
    }


    return (
<div class="flex flex-col items-center justify-center px-4 md:px-8 lg:px-16">
  <h1 class="text-2xl md:text-3xl lg:text-4xl font-bold mb-8 text-center">Welcome to the Landing Page!</h1>
  <div class="flex flex-col md:flex-row items-center justify-center md:justify-between mb-8 w-full">
    <button class="w-full md:w-auto mb-4 md:mb-0 md:mr-4 px-4 py-2 text-white bg-blue-500 rounded-md shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-600 focus:ring-opacity-50" onClick={handleLogOut}>Log Out</button>
    <button class="w-full md:w-auto px-4 py-2 text-white bg-green-500 rounded-md shadow-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-600 focus:ring-opacity-50" onClick={handleNewProject}>Create New Project</button>
  </div>
  <div class="flex flex-col md:flex-row justify-center w-full">
    <div class="w-full md:w-1/2 md:mr-2 mb-8 md:mb-0">
      <FeaturedTablature featuredTabs={featuredTabs} user={user} setProjectToView={setProjectToView}/>
    </div>
    <div class="w-full md:w-1/2 md:ml-2">
      <UserProjects userTabs={userTabs} user={user} setProjectToView={setProjectToView}/>
    </div>
  </div>
</div>
    )
}
export default LandingPage
