import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";
import ReviewCard from "./ReviewCard";

function Reviews({projectToView, setProjectToView}) {
    const [reviews, setReviews] = useState([])
    const [rating, setRating] = useState(0)
    const [review, setReview] = useState("")

    useEffect(() => {
        fetch(`/projects/${projectToView.id}`)
        .then((r) => r.json())
        .then((project) => {
            setProjectToView(project)
        })
    }, [])

    const reviewsToDisplay = projectToView?.reviews?.map((review) => {
        return <ReviewCard key={review.id} review={review}/>
    })

    return (
        <div className="flex flex-col items-center justify-center">
          <h1 className="text-4xl mb-4">Reviews</h1>
          <div className="w-full md:w-2/3 lg:w-1/2 xl:w-1/3">
            {reviewsToDisplay}
            <form className="bg-white rounded-lg shadow-md p-6 mt-4">
              <div className="mb-4">
                <label
                  htmlFor="rating"
                  className="block text-gray-700 font-bold mb-2"
                >
                  Rating
                </label>
                <input
                  type="number"
                  id="rating"
                  name="rating"
                  min="0"
                  max="5"
                  className="border rounded-md py-2 px-3 w-full"
                  value={rating}
                  onChange={(e) => setRating(e.target.value)}
                ></input>
              </div>
              <div className="mb-4">
                <label
                  htmlFor="review"
                  className="block text-gray-700 font-bold mb-2"
                >
                  Review
                </label>
                <textarea
                  id="review"
                  name="review"
                  className="border rounded-md py-2 px-3 w-full"
                ></textarea>
              </div>
              <button
                type="submit"
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Submit Review
              </button>
            </form>
          </div>
        </div>
      );
    }
export default Reviews
