import React, { useEffect } from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { isNumber } from "tone";

function CreateNewTabForm({ user, setProjectToView, setUserTabs, setFeaturedTabs, setUser}) {
    const navigate = useNavigate()
    const [formData, setFormData] = useState({
        title: "",
        artist: "",
        tuning: "",
        capo: "",
        bpm: "",
        user_id: user?.id

    })

    useEffect(() => {
        fetch("/check_session")
        .then((r) => {
            if (r.ok) {
                r.json().then((user) => {
                    setFeaturedTabs(user.all_tabs)
                    setUser(user)
                    if (user.tabs) {
                        setUserTabs(user.tabs)
                    }
                })
            } else {
                navigate("/login")
            }
        })
    }, [])


    const handleSubmit = (e) => {
        e.preventDefault()
        fetch("/tabs", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(formData)
        })
            .then((r) => r.json())
            .then((tab) => {
                console.log(tab)
                setProjectToView(tab)
                navigate(`/edit_project/${tab.id}`)
            }
            )
    }
    console.log(formData)
    return (
        <div className="flex h-screen items-center justify-center">
            <form className="md:flex h-1/2 w-1/4 text-md flex flex-col bg-slate-400 border border-gray-900 p-6 rounded-lg justify-center items-center text-center" onSubmit={(e) => handleSubmit(e)} >
                <input placeholder="Song Name" name="song" className="text-xl text-black text-center rounded-md mt-3" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })}></input>
                <input placeholder="Artist" name="artist" className="text-xl text-black text-center rounded-md mt-3" value={formData.artist} onChange={(e) => setFormData({ ...formData, artist: e.target.value })}></input>
                <input placeholder="Tuning" name="tuning" className="text-xl text-black text-center rounded-md mt-3" value={formData.tuning} onChange={(e) => setFormData({ ...formData, tuning: e.target.value })} ></input>
                <input placeholder="Capo" name="capo" className="text-xl text-black text-center rounded-md mt-3" value={formData.capo} onChange={(e) => setFormData({ ...formData, capo: e.target.value})} ></input>
                <input placeholder="BPM" name="bpm" className="text-xl text-black text-center rounded-md mt-3" value={formData.bpm} onChange={(e) => setFormData({ ...formData, bpm: e.target.value})}></input>
                <button className="flex mt-4 text-black bg-white w-1/4">Create Project</button>
            </form>
        </div>
    )

}

export default CreateNewTabForm
