import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";

function ProjectCard({tab, setProjectToView, user}){
    const navigate = useNavigate()

    const handleClick = () => {
        if (tab.user_id === user.id){
            setProjectToView(tab)
            navigate(`/edit_project/${tab.id}`)
        } else {
            setProjectToView(tab)
            navigate(`/project/${tab.id}`)
        }
    }
    return (
<div onClick={handleClick} class="border rounded-md w-48 sm:w-64 md:min-w-72 md:max-w-md xl:max-w-2xl p-2 shadow-lg hover:shadow-xl transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-105">
  <div class="text-xs sm:text-sm md:text-lg font-medium text-gray-700 mb-1">Artist:</div>
  <div class="text-base sm:text-lg md:text-2xl font-bold text-gray-900 mb-2 leading-tight">{tab.artist}</div>
  <div class="text-xs sm:text-sm md:text-lg font-medium text-gray-700 mb-1">Song:</div>
  <div class="text-base sm:text-lg md:text-2xl font-bold text-gray-900 mb-2 leading-tight">{tab.title}</div>
  <div class="text-xs sm:text-sm md:text-lg font-medium text-gray-700">Average Rating:</div>
  <div class="text-base sm:text-lg md:text-2xl font-bold text-gray-900 leading-tight">{tab.average_rating}</div>
</div>

    )
}

export default ProjectCard
