import React from "react";
import { NavLink } from "react-router-dom";

function NavBar({ user, projectToView }) {

    const handleLogOut = () => {
        fetch("/logout", {
            method: "POST",
        })
            .then(window.location.reload())
    }

    return (
        <>
            {user ? (
                <div className="left-0 top-0 h-screen flex flex-col bg-gradient-to-b from-gray-800 to-gray-900">
                    <nav className="flex flex-col flex-wrap items-center justify-between p-6">
                        <div className="flex items-center flex-shrink-0 text-white mr-6">
                            <NavLink to="/" className="text-2xl font-bold hover:text-gray-300 transition duration-200 ease-in-out">Logo</NavLink>
                        </div>
                        <div className="block lg:hidden">
                            <button className="flex items-center px-3 py-2 border rounded text-gray-200 border-gray-400 hover:text-white hover:border-white transition duration-200 ease-in-out">
                                <svg className="fill-current h-3 w-3" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><title>Menu</title><path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" /></svg>
                            </button>
                        </div>
                        <div className="w-full flex-col flex-grow lg:flex lg:items-center lg:w-auto hidden mt-2 lg:mt-0 lg:bg-transparent text-white lg:p-0 z-20">
                            <div className="text-sm flex flex-col lg:flex-grow">
                                <NavLink to="/" className="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-gray-300 mr-4 transition duration-200 ease-in-out">Home</NavLink>
                                <NavLink to="/create" className="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-gray-300 mr-4 transition duration-200 ease-in-out">Create</NavLink>
                                <NavLink to="/your_projects" className="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-gray-300 mr-4 transition duration-200 ease-in-out">Your Projects</NavLink>
                                {projectToView ? (
                                    <NavLink to="/reviews/:id" className="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-gray-300 mr-4 transition duration-200 ease-in-out">Reviews</NavLink>
                                ) : null}
                            </div>
                            {user ? (
                                <div className="inline-block text-sm px-4 py-2 leading-none border rounded text-white border-white hover:border-transparent hover:text-gray-900 hover:bg-white mt-4 lg:mt-0 transition duration-200 ease-in-out">
                                    <button onClick={handleLogOut} className="font-bold">Logout</button>
                                </div>
                            ) : null}
                        </div>
                    </nav>
                </div>
            ) : null

            }
        </>
    )
}

export default NavBar;
