import React, { useState } from "react";
import { NavLink } from "react-router-dom";

function NavBar({ user, projectToView }) {
    const [isClicked, setIsClicked] = useState(false)

    const handleLogOut = () => {
        fetch("/logout", {
            method: "POST",
        })
            .then(window.location.reload())
    }

    return (
        <>
        {user ? (
          <div>
            <button onClick={(e) => setIsClicked(!isClicked)}>DropDown</button>
            {isClicked ? (
              <div className="left-0 top-0 h-screen flex flex-col items-center transition-all duration-500 ease-in-out transform-gpu -translate-x opacity-100">
                <NavLink
                  to="/"
                  className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 hover:scale-110 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2"
                >
                  Home
                </NavLink>
                <NavLink
                  to="/create"
                  className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2"
                >
                  Create
                </NavLink>
                <NavLink
                  to="/your_projects"
                  className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2"
                >
                  Your Projects
                </NavLink>
                {projectToView ? (
                  <NavLink
                    to="/reviews/:id"
                    className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 transition duration-200 ease-in-out bg-gray-700 rounded-md shadow-md mx-2 my-2"
                  >
                    Reviews
                  </NavLink>
                ) : null}
                {user ? (
                  <button
                    onClick={handleLogOut}
                    className="text-2xl font-bold text-white hover:text-gray-300 py-2 px-4 rounded-md transition duration-200 ease-in-out bg-gray-700 shadow-md mx-2 my-2"
                  >
                    Logout
                  </button>
                ) : null}
              </div>
            ) : null}
          </div>
        ) : null}
      </>
    );

}

export default NavBar;
