import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";

function ProjectCard({tab, setProjectToView, user, setUserTabs, setUser, setFeaturedTabs}){
    const navigate = useNavigate()

    useEffect(() => {
        fetch("/check_session")
        .then((r) => {
            if (r.ok) {
                r.json().then((user) => {
                    setFeaturedTabs(user.all_tabs)
                    setUser(user)
                    if (user.tabs) {
                        setUserTabs(user.tabs)
                    }
                    setProjectToView(tab)
                })
            } else {
                navigate("/login")
            }
        })
    }, [])

    const handleClick = () => {
        if (tab.user_id === user.id){
            setProjectToView(tab)
            navigate("/edit_project")
        } else {
            setProjectToView(tab)
            navigate("/project")
        }
    }
    return (
        <div onClick={handleClick} className="border rounded-md w-48">
            <div>Artist: {tab.artist}</div>
            <div>Song: {tab.title}</div>
            <div>Average Rating: {tab.average_rating}</div>
        </div>
    )
}

export default ProjectCard
