import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";

function ProjectCard({tab, setProjectToView, user}){
    const navigate = useNavigate()

    const handleClick = () => {
        if (tab.user_id === user.id){
            setProjectToView(tab)
            navigate(`/edit_project/${tab.id}`)
        } else {
            setProjectToView(tab)
            navigate(`/project/${tab.id}`)
        }
    }
    return (
        <div onClick={handleClick} className="border rounded-md w-48 sm:w-64 md:w-72 lg:w-96 xl:w-120 2xl:w-144 p-2">
            <div className="text-sm sm:text-base md:text-lg font-medium mb-1">Artist: {tab.artist}</div>
            <div className="text-sm sm:text-base md:text-lg font-medium mb-1">Song: {tab.title}</div>
            <div className="text-sm sm:text-base md:text-lg font-medium">Average Rating: {tab.average_rating}</div>
        </div>
    )
}

export default ProjectCard
