import React from "react";
import { useState, useEffect } from "react";
import {useNavigate} from "react-router-dom";
import ReviewCard from "./ReviewCard";

function Reviews({projectToView, setProjectToView}) {

    useEffect(() => {
        fetch(`/projects/${projectToView.id}`)
        .then((r) => r.json())
        .then((project) => {
            setProjectToView(project)
        })
    }, [])

    const reviewsToDisplay = projectToView?.reviews?.map((review) => {
        return <ReviewCard review={review}/>
    })

    return (
        <div className="flex flex-col items-center justify-center">
            {reviewsToDisplay}
            <div>
                <form>
                    <label>Rating</label>
                    <input type="number" name="rating" min="0" max="5"></input>
                    <label>Review</label>
                    <input type="text" name="review"></input>
                    <input type="submit"></input>
                </form>
            </div>
        </div>
    )
}
export default Reviews
